sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   // Modified the if to return if the newValue == oldValue to avoid unecessary trips to the server
    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }

    // Instantiate the GetEmailAddress Script Include
	var getUs = new GlideAjax('GetUserCustomJson');
	// Specify the getEmail method
	getUs.addParam('sysparm_name', 'getUserJson');
	// Pass the Requested for sys_id
	getUs.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

	// Send request to the server
	getUs.getXML(showJsonResponse);

	// When response is back from the server
	function showJsonResponse(response){
		// Extract user custom data
		var resp = response.responseXML.documentElement.getAttribute('answer');
		g_form.addInfoMessage('resp: ' + ((resp == null)?'null':resp));
		// Transform the JSON string to an object
		var obj = resp.evalJSON();
		g_form.addInfoMessage('email: ' + obj.email);
		g_form.addInfoMessage('title: ' + obj.title);
	} 
}
