sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},

    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);

        // If the testdate is before rightnow, return true, else return false
        if (testdate.before(rightnow)) {
            return true;
        } 
		else {
            return false;
        }
    },

    isDateTodayOrPast: function(strDate) {
        // rightnow stores the current date
        var rightnow = new GlideDateTime().getLocalDate();//no time
        // Tested date
        var testdate = new GlideDateTime(strDate).getLocalDate();//no time

        // If the test date is before or equal to rightnow return true, else - false
        if (testdate.onOrBefore(rightnow)) {
            return true;
        }
		else{
			return false;
		}
    },

    type: 'NeedItUtils'
};
